import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { useEncryption } from '../../hooks/useEncryption';

const SecureRedirect = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { decrypt } = useEncryption();
  const [isRedirecting, setIsRedirecting] = useState(true);

  useEffect(() => {
    const redirect = async () => {
      const urlParams = new URLSearchParams(location.search);
      const encryptedTarget = urlParams.get('target');

      if (encryptedTarget) {
        try {
          const decryptedUrl = decrypt(encryptedTarget);
          
          // Validate if it's a safe internal route
          const safeRoutes = ['/', '/about', '/services', '/projects', '/contact'];
          if (safeRoutes.includes(decryptedUrl)) {
            // Use replace to prevent back button from showing secure route
            navigate(decryptedUrl, { replace: true });
          } else {
            // If not a safe route, redirect to home
            navigate('/', { replace: true });
          }
        } catch (error) {
          console.error('Decryption failed:', error);
          navigate('/', { replace: true });
        }
      } else {
        navigate('/', { replace: true });
      }
      setIsRedirecting(false);
    };

    // Small delay to ensure the component mounts before redirect
    const timer = setTimeout(redirect, 50);
    return () => clearTimeout(timer);
  }, [location, navigate, decrypt]);

  if (isRedirecting) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '50vh',
        opacity: 0,
        pointerEvents: 'none'
      }}>
        <div style={{ fontSize: '0px' }}>Redirecting...</div>
      </div>
    );
  }

  return null;
};

export default SecureRedirect;
