const CryptoJS = require('crypto-js');

const ENCRYPTION_KEY = process.env.REACT_APP_ENCRYPTION_KEY;

export const encryptUrl = (url) => {
  if (!ENCRYPTION_KEY) {
    console.warn('Encryption key not found');
    return url;
  }
  
  try {
    const encrypted = CryptoJS.AES.encrypt(url, ENCRYPTION_KEY).toString();
    return btoa(encrypted); // Base64 encode for URL safety
  } catch (error) {
    console.error('Encryption error:', error);
    return url;
  }
};

export const decryptUrl = (encryptedUrl) => {
  if (!ENCRYPTION_KEY) {
    console.warn('Encryption key not found');
    return encryptedUrl;
  }
  
  try {
    const decoded = atob(encryptedUrl); // Base64 decode
    const decrypted = CryptoJS.AES.decrypt(decoded, ENCRYPTION_KEY);
    return decrypted.toString(CryptoJS.enc.Utf8);
  } catch (error) {
    console.error('Decryption error:', error);
    return encryptedUrl;
  }
};
