// C:\Users\user.DESKTOP-9V4975E\const\src\pages\Services\Services.js
import React, { useState } from 'react';

const Services = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [activeGallery, setActiveGallery] = useState('all');

  const serviceCategories = [
    {
      id: 'road',
      title: 'Road Construction',
      icon: 'üõ£Ô∏è',
      services: [
        'Bitumen Road Work', 'Compound Wall', 'Color Code', 'Stud Installation',
        'Road Marking', 'CC Work', 'Water Tank', 'Mini Bridge', 'Drainage Systems',
        'Culvert Construction', 'Arch Construction', 'Wet Mix Spreading & Laying'
      ]
    },
    {
      id: 'building',
      title: 'Building Construction',
      icon: 'üèóÔ∏è',
      services: [
        'Commercial Buildings', 'Residential Complexes', 'Layout Development',
        'Structural Work', 'Finishing Work'
      ]
    },
    {
      id: 'vehicles',
      title: 'Vehicle Rentals',
      icon: 'üöõ',
      services: [
        'Pickup and 407', 'Earth Ramper', 'JCB', 'Baby Roller', 'Tandem Roller',
        'Joseph Roller', 'Paver', 'Tipper', 'Roller', 'Tanker Lorry', 'Soil Compactor',
        'Ajax', 'Tractor Mount Sprayer', 'Power rowel', 'Thar Plant'
      ]
    },
    {
      id: 'materials',
      title: 'Building Materials',
      icon: 'üèóÔ∏è',
      services: [
        'Cement Supply', 'TMT Steel Bars', 'Bricks & Blocks', 'Sand & Aggregates',
        'Construction Chemicals'
      ]
    }
  ];

  const cementBrands = ['Sankar - Vailmai', 'Ultratech', 'Ranco', 'Dolmia', 'Ambuja', 'Chattinad'];
  const tmtBrands = ['Tata - Indrola', 'Jsw - Amman', 'Vaizag - Jathi', 'Sali - Sun', 'Agni'];

  // Service Images arrays remain the same...
  const layoutImages = ['/uploads/our services/lAYOUT/ARCH.jpeg', /* ... */];
  const roadConstructionImages = ['/uploads/our services/ROAD CONSTRUCTION/4.1 GSB SPREADING AND LAYING.jpg', /* ... */];
  const buildingConstructionImages = ['/uploads/our services/BUILDING CONSTRUCTION/1.jpg', /* ... */];
  const vehicleImages = ['/uploads/our services/FOR HIRE/ajax.jpg', /* ... */];
  const materialImages = ['/uploads/our services/SALES AND SERVICES/BRICKS.jpeg', /* ... */];

  const allServiceImages = [
    ...layoutImages, ...roadConstructionImages, ...buildingConstructionImages, 
    ...vehicleImages, ...materialImages
  ];

  const getGalleryImages = () => {
    switch(activeGallery) {
      case 'layout': return layoutImages;
      case 'road': return roadConstructionImages;
      case 'building': return buildingConstructionImages;
      case 'vehicles': return vehicleImages;
      case 'materials': return materialImages;
      default: return allServiceImages;
    }
  };

  return (
    <div className="container">
      {/* Hero Section */}
      <section className="services-hero">
        <div className="hero-content">
          <h1>Our Services</h1>
          <p className="hero-subtitle">Comprehensive Construction Solutions Under One Roof</p>
        </div>
      </section>

      {/* Services Overview */}
      <section className="services-overview">
        <div className="section-header">
          <h2>What We Offer</h2>
          <p>Complete range of construction services and materials</p>
        </div>

        <div className="services-categories">
          {serviceCategories.map((category) => (
            <div key={category.id} className="service-category">
              <div className="category-header">
                <div className="category-icon">{category.icon}</div>
                <h3>{category.title}</h3>
              </div>
              <ul className="service-list">
                {category.services.map((service, index) => (
                  <li key={index}>{service}</li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      </section>

      {/* Specialized Services */}
      <section className="specialized-services">
        <div className="specialized-grid">
          <div className="specialized-card">
            <h3>Bitumen Plant Operations</h3>
            <p>We operate our own Bitumen Thar Plant to ensure quality control and timely project completion.</p>
            <div className="specialized-badge">Quality Assured</div>
          </div>

          <div className="specialized-card">
            <h3>Vehicle Fleet Management</h3>
            <p>Well-maintained equipment operated by highly skilled professionals for optimal performance.</p>
            <div className="specialized-badge">Well-Maintained</div>
          </div>
        </div>
      </section>

      {/* Materials Brands */}
      <section className="brands-section">
        <div className="brands-grid">
          <div className="brand-category">
            <h4>Cement Brands</h4>
            <div className="brand-list">
              {cementBrands.map((brand, index) => (
                <span key={index} className="brand-item">{brand}</span>
              ))}
            </div>
          </div>
          
          <div className="brand-category">
            <h4>TMT Steel Brands</h4>
            <div className="brand-list">
              {tmtBrands.map((brand, index) => (
                <span key={index} className="brand-item">{brand}</span>
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* Tamil Content */}
      <section className="tamil-section">
        <div className="tamil-content">
