const CryptoJS = require('crypto-js');

const ENCRYPTION_KEY = process.env.REACT_APP_ENCRYPTION_KEY || 'fallback-secure-key-2024';

export const encryptUrl = (url) => {
  if (!ENCRYPTION_KEY) {
    console.warn('Encryption key not found, using fallback');
    return btoa(url);
  }
  
  try {
    // Multiple encryption layers for enhanced security
    const encrypted1 = CryptoJS.AES.encrypt(url, ENCRYPTION_KEY).toString();
    const encrypted2 = CryptoJS.DES.encrypt(encrypted1, ENCRYPTION_KEY).toString();
    const finalEncrypted = btoa(encrypted2); // Base64 encode for URL safety
    
    return finalEncrypted;
  } catch (error) {
    console.error('Encryption error:', error);
    return btoa(url); // Fallback to base64
  }
};

export const decryptUrl = (encryptedUrl) => {
  if (!ENCRYPTION_KEY) {
    console.warn('Encryption key not found, using fallback');
    return atob(encryptedUrl);
  }
  
  try {
    // Reverse the encryption layers
    const decoded = atob(encryptedUrl); // Base64 decode
    const decrypted2 = CryptoJS.DES.decrypt(decoded, ENCRYPTION_KEY);
    const decrypted1 = decrypted2.toString(CryptoJS.enc.Utf8);
    const decrypted = CryptoJS.AES.decrypt(decrypted1, ENCRYPTION_KEY);
    
    return decrypted.toString(CryptoJS.enc.Utf8);
  } catch (error) {
    console.error('Decryption error:', error);
    return atob(encryptedUrl); // Fallback to base64 decode
  }
};
