import React from 'react';
import { useNavigate } from 'react-router-dom';

const SecureLink = ({ to, children, className, ...props }) => {
  const navigate = useNavigate();
  
  const handleClick = (e) => {
    e.preventDefault();
    
    // For internal routes, navigate directly
    if (to.startsWith('/')) {
      navigate(to, { replace: true });
    } else {
      // For external links, open in new tab/window
      window.open(to, '_blank', 'noopener,noreferrer');
    }
  };

  return (
    <a 
      href={to} 
      onClick={handleClick} 
      className={className} 
      {...props}
    >
      {children}
    </a>
  );
};

export default SecureLink;
