import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useEncryption } from '../../hooks/useEncryption';

const SecureLink = ({ to, children, className, ...props }) => {
  const { encrypt } = useEncryption();
  const navigate = useNavigate();
  
  const handleClick = (e) => {
    e.preventDefault();
    // Navigate immediately for internal links
    if (to.startsWith('/')) {
      navigate(to);
    } else {
      // For external links, open in new tab with encryption
      const encryptedUrl = encrypt(to);
      window.open(`/secure-redirect?token=${encodeURIComponent(encryptedUrl)}`, '_blank');
    }
  };

  // Generate encrypted href for security display
  const encryptedHref = to.startsWith('/') 
    ? `https://${window.location.hostname}/secure?target=${encodeURIComponent(encrypt(to))}`
    : `https://${window.location.hostname}/secure-redirect?token=${encodeURIComponent(encrypt(to))}`;

  return (
    <a 
      href={encryptedHref} 
      onClick={handleClick} 
      className={className}
      data-actual-url={to} // Store actual URL in data attribute
      {...props}
    >
      {children}
    </a>
  );
};

export default SecureLink;


