// C:\Users\user.DESKTOP-9V4975E\const\src\pages\Home\Home.js
import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
const Home = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [isVisible, setIsVisible] = useState(false);
  const [statsVisible, setStatsVisible] = useState(false); 

  useEffect(() => {
  window.scrollTo(0, 0);
}, []);

  useEffect(() => {
  const observer = new IntersectionObserver(
    (entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          setStatsVisible(true);
        }
      });
    },
    { threshold: 0.3 } // Trigger when 30% of the element is visible
  );

  if (statsRef.current) {
    observer.observe(statsRef.current);
  }

  return () => {
    if (statsRef.current) {
      observer.unobserve(statsRef.current);
    }
  };
}, []);
  useEffect(() => {
    setIsVisible(true);
  }, []);
useEffect(() => {
  if (statsVisible) {
    const duration = 2000;
    const steps = 60;
    const stepDuration = duration / steps;

    stats.forEach((stat, index) => {
      const targetNumber = parseInt(stat.number.replace('+', ''));
      let currentStep = 0;

      const counter = setInterval(() => {
        currentStep++;
        const progress = currentStep / steps;
        const currentValue = Math.floor(targetNumber * progress);

        setAnimatedStats(prev => {
          const newStats = [...prev];
          newStats[index] = `${currentValue}+`;
          return newStats;
        });

        if (currentStep >= steps) {
          clearInterval(counter);
          setAnimatedStats(prev => {
            const newStats = [...prev];
            newStats[index] = stat.number;
            return newStats;
          });
        }
      }, stepDuration);
    });
  }
