import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useEncryption } from '../../hooks/useEncryption';

const SecureLink = ({ to, children, className, ...props }) => {
  const { encrypt } = useEncryption();
  const navigate = useNavigate();
  
  const handleClick = (e) => {
    e.preventDefault();
    
    // For internal routes, navigate directly without showing intermediate URLs
    if (to.startsWith('/')) {
      // Navigate immediately to the target without encryption for internal routes
      navigate(to);
    } else {
      // For external links only, use encryption
      const encryptedUrl = encrypt(to);
      window.open(`/secure-redirect?token=${encodeURIComponent(encryptedUrl)}`, '_blank');
    }
  };

  // For internal links, use the actual URL but prevent default behavior
  // For external links, use a secure placeholder
  const href = to.startsWith('/') ? to : '#secure-external';

  return (
    <a 
      href={href} 
      onClick={handleClick} 
      className={className} 
      {...props}
    >
      {children}
    </a>
  );
};

export default SecureLink;

