import React, { useState } from 'react';

const Services = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [activeGallery, setActiveGallery] = useState('all');

  const roadServices = [
    'Layout Development',
    'Arch Construction',
    'Drainage Systems',
    'Compound Wall',
    'Culvert Construction',
    'CC Work',
    'Mini Bridge Construction',
    'Laying & Spreading WMM',
    'Road Marking',
    'Stud Installation',
    'Color Code Application'
  ];

  const vehicles = [
    'Tipper',
    'Pickup and 407',
    'JCB',
    'Ajax',
    'Roller',
    'Baby Roller',
    'Joshop Roller',
    'Tandem Roller',
    'Paver',
    'Soil Compactor',
    'Tractor Mount Sprayer',
    'Tanker Larry',
    'Power Traveller',
    'Earth Rammer'
  ];

  const cementBrands = [
    'Sankar - Vailmai',
    'Ultratech',
    'Ranco',
    'Dolmia',
    'Ambuja',
    'Chattinad'
  ];

  const tmtBrands = [
    'Tata - Indrola',
    'Jsw - Amman',
    'Vaizag - Jathi',
    'Sali - Sun',
    'Agni'
  ];

  // Service Images
  const layoutImages = [
    '/uploads/our services/lAYOUT/ARCH.jpeg',
    '/uploads/our services/lAYOUT/ARCH...jpeg',
    '/uploads/our services/lAYOUT/COLOR CODE.jpeg',
    '/uploads/our services/lAYOUT/COMPOUND WALL...jpeg',
    '/uploads/our services/lAYOUT/COMPOUND WALL.jpeg',
    '/uploads/our services/lAYOUT/Copy of COLOR CODE.jpeg',
    '/uploads/our services/lAYOUT/CULVERT.jpeg',
    '/uploads/our services/lAYOUT/LAYOUT.jpg',
    '/uploads/our services/lAYOUT/DRAINAGE.jpeg',
    '/uploads/our services/lAYOUT/MINI BRIDGE.jpeg',
    '/uploads/our services/lAYOUT/Road marking_.jpg',
    '/uploads/our services/lAYOUT/STUD.jpeg'
  ];

  const roadConstructionImages = [
    '/uploads/our services/ROAD CONSTRUCTION/4.1 GSB SPREADING AND LAYING.jpg',
    '/uploads/our services/ROAD CONSTRUCTION/BITUMEN CONCRET 11.jpg',
    '/uploads/our services/ROAD CONSTRUCTION/bitumen concrete.jpg',
    '/uploads/our services/ROAD CONSTRUCTION/bitumen macadam.jpg'
  ];

  const buildingConstructionImages = [
    '/uploads/our services/BUILDING CONSTRUCTION/1.jpg',
    '/uploads/our services/BUILDING CONSTRUCTION/2.jpg',
    '/uploads/our services/BUILDING CONSTRUCTION/BUILDING CONSTRUCTION.jpg'
  ];

  const vehicleImages = [
    '/uploads/our services/FOR HIRE/ajax.jpg',
    '/uploads/our services/FOR HIRE/BABY ROLLER.jpg',
    '/uploads/our services/FOR HIRE/jcb (2).jpg',
    '/uploads/our services/FOR HIRE/jcb.jpg',
    '/uploads/our services/FOR HIRE/JOSEPH  ROLLER.jpg',
    '/uploads/our services/FOR HIRE/paver.jpg',
    '/uploads/our services/FOR HIRE/pickup and 407.jpg',
    '/uploads/our services/FOR HIRE/roller.jpg',
    '/uploads/our services/FOR HIRE/SOIL COMPACTOR.jpg',
    '/uploads/our services/FOR HIRE/TANDEM ROLLER.jpg',
    '/uploads/our services/FOR HIRE/TRACTOR MOUNT SPRAYER.jpg',
    '/uploads/our services/FOR HIRE/vehicles.jpg'
  ];

  const materialImages = [
    '/uploads/our services/SALES AND SERVICES/BRICKS.jpeg',
    '/uploads/our services/SALES AND SERVICES/FLY ASH BRICKS.jpeg',
    '/uploads/our services/SALES AND SERVICES/HOLLOW BLOCKS.jpeg',
    '/uploads/our services/SALES AND SERVICES/SOLID BLOCKS.jpeg'
  ];

  const allServiceImages = [
    ...layoutImages,
    ...roadConstructionImages,
    ...buildingConstructionImages,
    ...vehicleImages,
    ...materialImages
  ];

  const getGalleryImages = () => {
    switch(activeGallery) {
      case 'layout': return layoutImages;
      case 'road': return roadConstructionImages;
      case 'building': return buildingConstructionImages;
      case 'vehicles': return vehicleImages;
      case 'materials': return materialImages;
      default: return allServiceImages;
    }
  };

  return (
    <div className="container">
      {/* Cover Image */}
      <section style={{ marginBottom: '3rem' }}>
        <img 
          src="/uploads/Our Services cover pic/Place it as cover photo of our services_.jpg" 
          alt="Our Services Cover"
          style={{
            width: '100%',
            height: '300px',
            objectFit: 'cover',
            borderRadius: '10px'
          }}
        />
      </section>

      <section className="page-section">
        <h1 className="section-title">Our Services</h1>
        <div className="section-content">
          
          <div style={{ marginBottom: '3rem' }}>
            <h2>Road Construction Services</h2>
            <p>We specialize in comprehensive road construction services including:</p>
            <ul style={{ marginTop: '1rem', paddingLeft: '2rem' }}>
              {roadServices.map((service, index) => (
                <li key={index} style={{ marginBottom: '0.5rem' }}>{service}</li>
              ))}
            </ul>
          </div>

          <div style={{ marginBottom: '3rem', padding: '1.5rem', backgroundColor: '#f8f9fa', borderRadius: '8px' }}>
            <h3>Bitumen Plant</h3>
            <p>We operate our own Bitumen Thar Plant to ensure quality control and timely project completion.</p>
          </div>

          <div style={{ marginBottom: '3rem' }}>
            <h2>Vehicle Rentals</h2>
            <p>We supply all essential construction vehicles and machinery on rent:</p>
            <ul style={{ marginTop: '1rem', paddingLeft: '2rem' }}>
              {vehicles.map((vehicle, index) => (
                <li key={index} style={{ marginBottom: '0.5rem' }}>{vehicle}</li>
              ))}
            </ul>
            <p><em>Our fleet is well-maintained and operated by highly skilled professionals.</em></p>
          </div>

          <div style={{ marginBottom: '3rem' }}>
            <h2>Building Materials</h2>
            
            <div style={{ marginBottom: '2rem' }}>
              <h3>Wholesale Cement Dealer</h3>
              <p>Authorized dealer for quality cement brands:</p>
              <ul style={{ marginTop: '1rem', paddingLeft: '2rem' }}>
                {cementBrands.map((brand, index) => (
                  <li key={index} style={{ marginBottom: '0.5rem' }}>{brand}</li>
                ))}
              </ul>
            </div>

            <div>
              <h3>TMT Steel Bars Dealer</h3>
              <p>Authorized dealer for premium TMT brands:</p>
              <ul style={{ marginTop: '1rem', paddingLeft: '2rem' }}>
                {tmtBrands.map((brand, index) => (
                  <li key={index} style={{ marginBottom: '0.5rem' }}>{brand}</li>
                ))}
              </ul>
            </div>
          </div>

          {/* Service Gallery */}
          <div style={{ marginTop: '3rem' }}>
            <h2>Our Service Gallery</h2>
            
            {/* Gallery Filter */}
            <div style={{ marginBottom: '2rem', textAlign: 'center' }}>
              <button 
                onClick={() => setActiveGallery('all')}
                style={{
                  padding: '8px 16px',
                  margin: '0 5px',
                  backgroundColor: activeGallery === 'all' ? '#007bff' : '#f8f9fa',
                  color: activeGallery === 'all' ? 'white' : '#333',
                  border: '1px solid #ddd',
                  borderRadius: '5px',
                  cursor: 'pointer'
                }}
              >
                All Services
              </button>
              <button 
                onClick={() => setActiveGallery('layout')}
                style={{
                  padding: '8px 16px',
                  margin: '0 5px',
                  backgroundColor: activeGallery === 'layout' ? '#007bff' : '#f8f9fa',
                  color: activeGallery === 'layout' ? 'white' : '#333',
                  border: '1px solid #ddd',
                  borderRadius: '5px',
                  cursor: 'pointer'
                }}
              >
                Layout
              </button>
              <button 
                onClick={() => setActiveGallery('road')}
                style={{
                  padding: '8px 16px',
                  margin: '0 5px',
                  backgroundColor: activeGallery === 'road' ? '#007bff' : '#f8f9fa',
                  color: activeGallery === 'road' ? 'white' : '#333',
                  border: '1px solid #ddd',
                  borderRadius: '5px',
                  cursor: 'pointer'
                }}
              >
                Road Construction
              </button>
              <button 
                onClick={() => setActiveGallery('building')}
                style={{
                  padding: '8px 16px',
                  margin: '0 5px',
                  backgroundColor: activeGallery === 'building' ? '#007bff' : '#f8f9fa',
                  color: activeGallery === 'building' ? 'white' : '#333',
                  border: '1px solid #ddd',
                  borderRadius: '5px',
                  cursor: 'pointer'
                }}
              >
                Building Construction
              </button>
              <button 
                onClick={() => setActiveGallery('vehicles')}
                style={{
                  padding: '8px 16px',
                  margin: '0 5px',
                  backgroundColor: activeGallery === 'vehicles' ? '#007bff' : '#f8f9fa',
                  color: activeGallery === 'vehicles' ? 'white' : '#333',
                  border: '1px solid #ddd',
                  borderRadius: '5px',
                  cursor: 'pointer'
                }}
              >
                Vehicles
              </button>
              <button 
                onClick={() => setActiveGallery('materials')}
                style={{
                  padding: '8px 16px',
                  margin: '0 5px',
                  backgroundColor: activeGallery === 'materials' ? '#007bff' : '#f8f9fa',
                  color: activeGallery === 'materials' ? 'white' : '#333',
                  border: '1px solid #ddd',
                  borderRadius: '5px',
                  cursor: 'pointer'
                }}
              >
                Materials
              </button>
            </div>

            {/* Gallery Images */}
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
              gap: '1rem'
            }}>
              {getGalleryImages().map((image, index) => (
                <div 
                  key={index} 
                  style={{
                    cursor: 'pointer',
                    borderRadius: '8px',
                    overflow: 'hidden',
                    boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
                  }}
                  onClick={() => setSelectedImage(image)}
                >
                  <img 
                    src={image} 
                    alt={`Service ${index + 1}`}
                    style={{
                      width: '100%',
                      height: '200px',
                      objectFit: 'cover'
                    }}
                  />
                </div>
              ))}
            </div>
          </div>
        </div>
      </section>




      {/* Image Modal */}
      {selectedImage && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          backgroundColor: 'rgba(0,0,0,0.8)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 1000
        }} onClick={() => setSelectedImage(null)}>
          <img 
            src={selectedImage} 
            alt="Enlarged view"
            style={{
              maxWidth: '90%',
              maxHeight: '90%',
              objectFit: 'contain'
            }}
          />
        </div>
      )}
    </div>
  );
};

export default Services;
